mapper.select:
select mname
from major_t
where school = #{arg0}

select num
from class_t c
where cid = #{arg0}

select cno
from course_t
where cname = #{arg0}

select tno
from teacher_t
where tname = #{arg0}

select mno
from major_t
where mname = #{arg0}

select mno
from major_t
where mshort = #{arg0}
